const app = new PIXI.Application({ background: '#889966' });
document.body.appendChild(app.view);

const container = new PIXI.Container();

app.stage.addChild(container);

const texture = PIXI.Texture.from('atirador.png')

const atirador = new PIXI.Sprite(texture);
atirador.anchor.set(0.5)
atirador.rotation = 270 * Math.PI / 180;
atirador.scale.set(0.75)

container.addChild(atirador);

atirador.x = 80
atirador.y = 530

let speed = 10; 

window.addEventListener('keydown', (event) => {
  if (event.key === 'a' || event.key === 'A') { 
    atirador.x -= speed; 
  } else if (event.key === 'd' || event.key === 'D') { 
    atirador.x += speed; 
  }
});

const projetil = PIXI.Texture.from('projetil.png')

projetil.x = atirador.x
projetil.y = atirador.y

container.appendChild(projetil);

window.addEventListener('keydown', (event) => {
    if(event.key == "Space"){

    }
})